import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import Notification from "./components/UI/Notification";
import { uiActions } from "./store/ui-slice";

let isInitial = true;

function App() {
  const cartIsVisible = useSelector((state) => state.ui.cartIsVisible);
  const item = useSelector((state) => state.item);
  const dispatch = useDispatch();
  const notification = useSelector((state) => state.ui.notification);

  const trackData = useSelector((state) => state.ui);
  console.log(trackData);

  const BASE_URL =
    "https://react-movie-eb9a3-default-rtdb.firebaseio.com/cart.jon";
  useEffect(() => {
    const sendItemData = async () => {
      dispatch(
        uiActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sending cart data!",
        })
      );
      const response = await fetch(BASE_URL, {
        method: "PUT",
        body: JSON.stringify(item),
      });

      if (!response.ok) {
      }

      dispatch(
        uiActions.showNotification({
          status: "succeess",
          title: "Success!...",
          message: "Sent item data successfully!",
        })
      );
    };

    // if (isInitial) {
    //   isInitial = false;
    //   return;
    // }

    sendItemData().catch((error) => {
      dispatch(
        uiActions.showNotification({
          status: "error",
          title: "Error",
          message: "Sending cart data faild",
        })
      );
    });
  }, [item, dispatch]);

  console.log(item);
  return (
    <>
      {notification && (
        <Notification
          status={notification.status}
          title={notification.title}
          message={notification.message}
        />
      )}
      <Layout>
        {cartIsVisible && <Cart />}
        <Products />
      </Layout>
    </>
  );
}

export default App;
