const { createSlice } = require("@reduxjs/toolkit");

const itemSlice = createSlice({
  name: "item",
  initialState: { item: "book", amount: 10, many: 0 },
  reducers: {
    addItem(state) {
      state.many++;
    },
    minusItem(state) {
      state.many--;
    },
  },
});

export const itemAction = itemSlice.actions;

export default itemSlice;
